import React from "react";
import styled from "styled-components";

// 로고, 아이콘, 이미지 참조
import sevLogo from "../assets/img/sev_logo@2x.png";
import loginIcon from "../assets/img/ico-top-login@2x.png";
import joinIcon from "../assets/img/ico-top-join@2x.png";
import allMenuIcon from "../assets/img/ico-allmenu.png";
import searchIcon from "../assets/img/ico-search.png";

// header 스타일 블럭
const HeaderContainer = styled.header`
  width: 1280px;
  height: 125px;
  margin: 0 auto;
`;

// util 스타일 블럭
const UtilContainer = styled.div`
  width: 100%;
  height: 41px;
  border: 1px solid black;
  display: flex;
  justify-content: flex-end;

  ul {
    display: flex;

    li {
      img {
        width: 26px;
      }
    }
  }
`;

// gnb 스타일 블럭
const GnbContainer = styled.div`
  width: 100%;
  height: 84px;
  border: 1px solid black;
  display: flex;
  justify-content: space-between;
  align-items: center;
  
  h1 {
    .sevLogo {
      width: 231px;
      height: 40px;
      object-fit: cover;
    }
  }
  ul {
    display: flex;

    li {
      line-height: 84px;
      a {
        display: block;
        text-align: center;
        font-size: 26px;
        padding: 0 33px;

        .allMenuIcon {
          height: 20px;
        }
        .searchIcon {
          height: 24px;
        }
      }
    }
    li:nth-child(5) {
      padding: 0;
    }
    li:nth-child(6) {
      padding: 0;
      margin-left: 20px;
    }
  }
`;

const Header = () => {
  return (
    <HeaderContainer>
      <UtilContainer>
        <ul>
          <li>
            <a>
              <img src={loginIcon} alt="login_icon" />
            </a>
          </li>
          <li>
            <a>
              <img src={joinIcon} alt="join_icon" />
            </a>
          </li>
        </ul>
      </UtilContainer>
      <GnbContainer>
        <h1>
          <img src={sevLogo} alt="header_logo" className="sevLogo" />
        </h1>
        <ul>
          <li>
            <a>환자/보호자</a>
          </li>
          <li>
            <a>의료인</a>
          </li>
          <li>
            <a>공감 Story</a>
          </li>
          <li>
            <a>건강정보</a>
          </li>
          <li>
            <a>
              <img src={allMenuIcon} alt="all_menu_icon" className="allMenuIcon" />
            </a>
          </li>
          <li>
            <a>
              <img src={searchIcon} alt="search_icon" className="searchIcon" />
            </a>
          </li>
        </ul>
      </GnbContainer>
    </HeaderContainer>
  );
};

export default Header;
