/** import */
import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

/** 컴포넌트 참조 */
import Header from "../../components/Header";
import Footer from "../../components/Footer";

/** 이미지 참조 */
// 메인 슬라이드
import MainImage from "../../assets/img/img-visual-patient1.jpg";
// 병원 바로가기 아이콘
import ExternalLink from "../../assets/img/ico-external-link-white@2x.png";
// 바로가기 메뉴 아이콘
import ShortcutExpert from "../../assets/img/ico-shortcut-expert@2x.png";
import ShortcutDepartment from "../../assets/img/ico-shortcut-department@2x.png";
import ShortcutCalendar from "../../assets/img/ico-shortcut-calendar@2x.png";
import ShortcutRecervation from "../../assets/img/ico-shortcut-reservation@2x.png";
// 힘내요, 세브란스, 전공의 모집
import Cheerup from "../../assets/img/main-cheerup.jpg";
import Recruitment from "../../assets/img/img-recruitment.png";

/** 메인 스타일 */
const Main = styled.main`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  overflow-x: hidden;
`;

/** 이미지 슬라이드 스타일 */
const SlideSection = styled.section`
  width: 1920px;
  height: 500px;
  position: relative;

  article {
    position: absolute;
    top: 50%;
    left: 320px;
    transform: translate(0, -50%);

    .slide_title {
      font-size: 66px;
      line-height: 72px;
      font-family: "NanumSquare";
      color: white;
    }

    .slide_text {
      font-size: 24px;
      line-height: 32px;
      font-family: "NanumSquare";
      color: white;
      display: block;
      margin-top: 15px;
    }
  }
`;

/** 카테고리별 병원 바로가기 스타일 */
const HospitalSection = styled.section`
  width: 1920px;
  padding-bottom: 60px;
  background: url(./img/bg-main-pattern.png) no-repeat center /cover;
  display: flex;
  flex-direction: column;
  align-items: center;

  // 연세암병원, 심장혈관병원, ...
  .exteralLinks {
    width: 1280px;
    height: 73px;
    ul {
      width: 100%;
      height: 100%;
      display: flex;

      li {
        flex: 1 1 0;
        height: 100%;

        a {
          width: 100%;
          height: 100%;
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 0 20px 0 30px;
          box-sizing: border-box;

          span {
            font-size: 24px;
            color: white;
          }

          img {
            width: 20px;
            height: 20px;
          }
        }
        &:first-child {
          background-color: #2faaff;
        }
        &:nth-child(2) {
          background-color: #0094fb;
        }
        &:nth-child(3) {
          background-color: #007cfb;
        }
        &:nth-child(4) {
          background-color: #0054d1;
        }
        &:last-child {
          background-color: #003d7d;
        }
      }
    }
  }

  // 의료진 찾기, 진료과 찾기, ...
  .shortcutSlider {
    width: 1280px;
    height: 194px;
    padding: 58px 0;
    box-sizing: border-box;

    ul {
      width: 100%;
      height: 100%;
      display: flex;

      li {
        width: 25%;
        height: 100%;

        a {
          width: 100%;
          height: 100%;
          display: flex;

          img {
            width: 60px;
            height: 60px;
            padding: 8px 22.5px;
          }

          .textBox {
            display: flex;
            flex-direction: column;
            justify-content: space-between;

            strong {
              font-size: 24px;
              line-height: 24px;
            }

            span {
              font-size: 16px;
              line-height: 22px;
              color: #333333;
            }
          }
        }
      }
    }
  }

  // 공감story, 건강정보, ...
  .infoSlider {
    width: 1280px;
    display: flex;
    justify-content: space-between;

    dl {
      width: 297px;
      height: 360px;
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: space-between;

      dt {
        font-size: 30px;
        font-weight: bold;
        color: white;
        margin-top: 80px;

        ::after {
          content: "";
          display: block;
          margin: 20px auto 0;
          width: 40px;
          height: 1px;
          display: flex;
          background-color: white;
        }
      }

      dd {
        width: 260px;
        height: 173px;
        background-color: white;
        margin-bottom: 18.5px;
      }
    }

    a {
      width: 297px;
      height: 360px;

      img {
        width: 100%;
        height: 100%;
      }
    }
  }
`;

/** NEWS 스타일 */
const NewsSection = styled.section`
  width: 100%;
  height: 391px;
  padding: 80px 0;
  box-sizing: border-box;
  background-color: #003d7d;

  .newsArticleBox {
    width: 1280px;
    height: 211px;
    margin: 0 auto;
    display: flex;
    padding-top: 20px;
    overflow: hidden;

    .news {
      width: 210px;
      height: 211px;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      margin-right: 117px;

      h3 {
        font-size: 48px;
        line-height: 48px;
        color: white;
        display: flex;
        justify-content: space-between;
        align-items: center;

        a {
          display: block;
          width: 27px;
          height: 27px;
          background: url(./img/btn-more-plus.jpg) no-repeat /cover;
        }
      }

      p {
        font-size: 16px;
        line-height: 26px;
        color: white;
      }

      .btns {
        margin-top: 15px;
        .left_btn {
          display: block;
          width: 60px;
          height: 60px;
          background: url(./img/btn-left-white.png);
          border: none;
          float: left;
        }
        .right_btn {
          display: block;
          float: left;
          width: 60px;
          height: 60px;
          margin-left: 20px;
          background: url(./img/btn-right-white.png);
          border: none;

          &::after {
            content: "";
            float: none;
            display: block;
            clear: both;
          }
        }
      }
    }

    .newsBox {
      width: 297px;
      height: 211px;
      background-color: white;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      padding: 30px 30px 28px;
      margin-right: 30px;
      box-sizing: border-box;
      position: relative;

      .newsCategory {
        display: block;
        position: absolute;
        top: -16px;
        left: 30px;
        width: 78px;
        height: 40px;
        padding: 0 10px;
        box-sizing: border-box;
        background-color: #ac47d1;
        font-size: 14px;
        line-height: 40px;
        color: white;
      }

      .newsContentTitle {
        font-size: 18px;
        line-height: 28px;
      }

      .newsDate {
        font-size: 16px;
        line-height: 16px;
      }
    }
  }
`;

/** 하단 배너 스타일 */
const BannerSection = styled.section`
  width: 100%;
  height: 614px;
  padding: 65px 0 80px;
  box-sizing: border-box;
  background: url(./img/bg-mkt-banner.jpg) no-repeat center /cover;
`;

/** 상단 배너 스타일 */
const TopBannerSection = styled.section`
  width: 100%;
  height: 90px;
  background-color: #0054d1;

  .top_banner_content {
    width: 1280px;
    height: 100%;

    .tel_yellow {
      display: block;
      width: 52px;
      height: 52px;
      background: url(./img/ico-tel-yellow@2x.png) no-repeat center /cover;
    }
  }
`;

const MainPage = () => {
  return (
    <>
      <TopBannerSection>
        <div className="top_banner_content">
          <i className="tel_yellow" />
          <span>진료예약</span>
          <a href="/">1599-1004</a>
          <span>해외 수신번호(+82-2-2228-1004)</span>
          <i className="tel_primary" />

        </div>
      </TopBannerSection>

      <Header />
      <Main>
        <SlideSection>
          <img src={MainImage} alt="main_img" />
          <article>
            <span className="slide_title">
              <strong>공감,</strong> 또 하나의 치료
            </span>
            <span className="slide_text">질병 치료를 넘어 환자의 마음까지 치유하겠습니다.</span>
          </article>
        </SlideSection>

        <HospitalSection>
          <div className="exteralLinks">
            <ul>
              <li>
                <a href="https://cancer.severance.healthcare/cancer/index.do" target="_black" rel="noopener noreferrer">
                  <span>연세암병원</span>
                  <img src={ExternalLink} />
                </a>
              </li>
              <li>
                <a href="https://sev-heart.severance.healthcare/sev-heart/index.do" target="_black" rel="noopener noreferrer">
                  <span>심장혈관병원</span>
                  <img src={ExternalLink} />
                </a>
              </li>
              <li>
                <a href="https://sev-children.severance.healthcare/sev-children/index.do" target="_black" rel="noopener noreferrer">
                  <span>어린이병원</span>
                  <img src={ExternalLink} />
                </a>
              </li>
              <li>
                <a href="https://sev-eye.severance.healthcare/sev-eye/index.do" target="_black" rel="noopener noreferrer">
                  <span>안과병원</span>
                  <img src={ExternalLink} />
                </a>
              </li>
              <li>
                <a href="https://sev-rehabil.severance.healthcare/sev-rehabil/index.do" target="_black" rel="noopener noreferrer">
                  <span>재활병원</span>
                  <img src={ExternalLink} />
                </a>
              </li>
            </ul>
          </div>
          <div className="shortcutSlider">
            <ul>
              <li>
                <Link to="/">
                  <img src={ShortcutExpert} />
                  <div className="textBox">
                    <strong>의료진 찾기</strong>
                    <span>
                      전문 의료진을 빠르게
                      <br />
                      찾을 수 있습니다.
                    </span>
                  </div>
                </Link>
              </li>
              <li>
                <Link to="/">
                  <img src={ShortcutDepartment} />
                  <div className="textBox">
                    <strong>진료과 찾기</strong>
                    <span>
                      진료 전 진료과를
                      <br />
                      안내해 드립니다.
                    </span>
                  </div>
                </Link>
              </li>
              <li>
                <Link to="/">
                  <img src={ShortcutCalendar} />
                  <div className="textBox">
                    <strong>진료 예약</strong>
                    <span>
                      회원/비회원 편리하게
                      <br />
                      예약할 수 있습니다.
                    </span>
                  </div>
                </Link>
              </li>
              <li>
                <Link to="/">
                  <img src={ShortcutRecervation} />
                  <div className="textBox">
                    <strong>예약현황</strong>
                    <span>
                      회원 및 비회원 예약
                      <br />
                      내역을 볼 수 있습니다.
                    </span>
                  </div>
                </Link>
              </li>
            </ul>
          </div>
          <div className="infoSlider">
            <dl style={{ backgroundImage: "url(./img/bg-sympathy-story.jpg) no-repeat center /cover" }}>
              <dt>공감Story</dt>
              <dd>
                <div></div>
              </dd>
            </dl>
            <dl style={{ backgroundImage: "url(./img/bg-health-info.jpg)" }}>
              <dt>건강정보</dt>
              <dd>
                <div></div>
              </dd>
            </dl>
            <a href="https://yuhs.severance.healthcare/yuhs/history/museum/cheerupsev.do">
              <img src={Cheerup} alt="박기호 사진전 힘내요, 세브란스 세브란스 코로나 병동의 기록" />
            </a>
            <a href="https://recruit.severance.healthcare/recruit/recruit.do">
              <img src={Recruitment} alt="2023년 세브란스병원 전공의 (인턴 및 레지던트 1년차) 모집" />
            </a>
          </div>
        </HospitalSection>

        <NewsSection>
          <div className="newsArticleBox">
            <article className="news">
              <h3>
                <strong>NEWS</strong>
                <Link to="/news_home" />
              </h3>
