import React from "react";
import styled from "styled-components";

/** 컴포넌트 참조 */
import Header from "../../components/Header";
import Footer from "../../components/Footer";

/** 이미지 참조 */
import MainImage from "../../assets/img/img-visual-patient1.jpg";
import ExternalLink from "../../assets/img/ico-external-link-white@2x.png";
import { Link, Route, Router } from "react-router-dom";

/** 메인 스타일 블럭 */
const MainContainer = styled.main`
  width: 100%;
  background-color: #eee;
`;

/** 이미지 슬라이드 스타일 블럭 */
const SlideContainer = styled.figure`
  width: 1920px;
  height: 500px;
  margin: 0 auto;
`;

/** 병원 바로가기 스타일 */
const HospitalContainer = styled.div`

`;

const MainPage = () => {
  return (
    <>
      <Header />
      <MainContainer>
        <SlideContainer>
          <img src={MainImage} alt="main_img" />
        </SlideContainer>

        <HospitalContainer>
          <ul>
            <li><a href="https://cancer.severance.healthcare/cancer/index.do">연세암병원<img src={ExternalLink}/></a></li>
            <li><a href="https://sev-heart.severance.healthcare/sev-heart/index.do">심장혈관병원</a></li>
            <li><a href="https://sev-children.severance.healthcare/sev-children/index.do">어린이병원</a></li>
            <li><a href="https://sev-eye.severance.healthcare/sev-eye/index.do">안과병원</a></li>
            <li><a href="https://sev-rehabil.severance.healthcare/sev-rehabil/index.do">재활병원</a></li>
          </ul>
        </HospitalContainer>
      </MainContainer>

      <Footer />
    </>
  );
};

export default MainPage;
