import React from "react";
import styled from "styled-components";

/** 링크 */
import { Link } from "react-router-dom";

/** 로고, 아이콘, 이미지 참조 */
import sevLogo from "../assets/img/sev_logo@2x.png";
import loginIcon from "../assets/img/ico-top-login@2x.png";
import joinIcon from "../assets/img/ico-top-join@2x.png";
import allMenuIcon from "../assets/img/ico-allmenu.png";
import searchIcon from "../assets/img/ico-search.png";

/** 헤더 스타일 */
const HeaderContainer = styled.header`
  width: 1280px;
  height: 125px;
  margin: 0 auto;
`;

/** 사용자, 회원가입 아이콘 스타일 */
const UtilContainer = styled.div`
  width: 100%;
  height: 41px;
  display: flex;
  justify-content: flex-end;
  padding-top: 16px;
  box-sizing: border-box;

  ul {
    display: flex;
    li {
      &:nth
      margin-right: 20px;
      img {
        width: 22px;
      }
    }
  }
`;

/** 메인 네비게이션 스타일 */
const GnbContainer = styled.div`
  width: 100%;
  height: 84px;
  display: flex;
  justify-content: space-between;
  align-items: center;

  h1 {
    .sevLogo {
      width: 231px;
      height: 40px;
      object-fit: cover;
    }
  }
  ul {
    display: flex;

    li {
      display: flex;
      align-items: center;
      a {
        img {
        }
        .allMenuIcon {
          height: 20px;
        }
        .searchIcon {
          height: 24px;
        }
      }
      &:nth-child(5) {
        margin-left: 20px;
      }
      &:nth-child(6) {
        margin-left: 20px;
      }
    }
  }
`;

/** 메인 네비게이션 텍스트 스타일 */
const MenuArticle = styled(Link)`
  text-align: center;
  font-size: 26px;
  padding: 0 33px;
  line-height: 84px;
`;

const Header = () => {
  return (
    <HeaderContainer>
      <UtilContainer>
        <ul>
          <li>
            <Link to="/my_page">
              <img src={loginIcon} alt="login_icon" />
            </Link>
          </li>
          <li>
            <Link to="/join_page">
              <img src={joinIcon} alt="join_icon" />
            </Link>
          </li>
        </ul>
      </UtilContainer>

      <GnbContainer>
        <h1>
          <Link to="/">
            <img src={sevLogo} alt="header_logo" className="sevLogo" />
          </Link>
        </h1>
        <ul>
          <li>
            <MenuArticle to="/">환자/보호자</MenuArticle>
          </li>
          <li>
            <MenuArticle to="/">의료인</MenuArticle>
          </li>
          <li>
            <MenuArticle to="/">공감 Story</MenuArticle>
          </li>
          <li>
            <MenuArticle to="/">건강정보</MenuArticle>
          </li>
          <li>
            <Link to="/all_maenu_page">
              <img src={allMenuIcon} alt="all_menu_icon" className="allMenuIcon" />
            </Link>
          </li>
          <li>
            <Link to="/all_maenu_page">
              <img src={searchIcon} alt="search_icon" className="searchIcon" />
            </Link>
          </li>
        </ul>
      </GnbContainer>
    </HeaderContainer>
  );
};

export default Header;
