import React from "react";
import styled from "styled-components";

/** 컴포넌트 참조 */
import Header from "../../components/Header";
import Footer from "../../components/Footer";

/** 이미지 참조 */
import MainImage from "../../assets/img/img-visual-patient1.jpg";
import ExternalLink from "../../assets/img/ico-external-link-white@2x.png";
import { Link, Route, Router } from "react-router-dom";

/** 메인 스타일 블럭 */
const Main = styled.main`
  width: 100%;
`;

/** 이미지 슬라이드 스타일 블럭 */
const SlideFigure = styled.figure`
  width: 1920px;
  height: 500px;
  margin: 0 auto;
`;

/** 병원 바로가기 스타일 */
const HospitalSection = styled.section`
  width: 1280px;
  height: 73px;
  margin: 0 auto;

  ul {
    width: 100%;
    height: 100%;
    display: flex;

    li {
      width: 25%;
      height: 100%;
      background-color: #dbdbdb;
      a {
        width: 100%;
        height: 100%;
        display: flex;
        justify-content: space-between;
        padding: 0 20px 0 30px;
        box-sizing: border-box;

        span {
          font-size: 24px;
          color: white;
        }
        
        img {
          width: 20px;
          height: 20px;
        }
      }
    }
  }
`;

const MainPage = () => {
  return (
    <>
      <Header />
      <Main>
        <SlideFigure>
          <img src={MainImage} alt="main_img" />
        </SlideFigure>

        <HospitalSection>
          <ul>
            <li><a href="https://cancer.severance.healthcare/cancer/index.do"><sapn>연세암병원</sapn><img src={ExternalLink}/></a></li>
            <li><a href="https://sev-heart.severance.healthcare/sev-heart/index.do"><span>심장혈관병원</span><img src={ExternalLink}/></a></li>
            <li><a href="https://sev-children.severance.healthcare/sev-children/index.do"><sapn>어린이병원</sapn><img src={ExternalLink}/></a></li>
            <li><a href="https://sev-eye.severance.healthcare/sev-eye/index.do"><span>안과병원</span><img src={ExternalLink}/></a></li>
            <li><a href="https://sev-rehabil.severance.healthcare/sev-rehabil/index.do"><sapn>sdad</sapn><img src={ExternalLink}/></a></li>
          </ul>
        </HospitalSection>
      </Main>

      <Footer />
    </>
  );
};

export default MainPage;
